mapper.update:
UPDATE t_question_bank SET 1 = 1,
bank_name = #{bank.bankName}, description = #{bank.description},
WHERE id = #{id}

mapper.select:
SELECT count(*)
FROM t_question_bank bank LEFT JOIN t_question tq ON bank.id = tq.bank_id
WHERE tq.type = #{type}

SELECT *
FROM t_question_bank bank LEFT JOIN t_question tq ON bank.id = tq.bank_id
WHERE tq.type = #{type}
